import React, { useEffect, useState } from "react";
import "./CountrySearch.css";

const CountrySearch = ({countries}) => {
    const [searchTerm, setSearchTerm] = useState("");
    const [byContinent, setByContinent] = useState(false);
    const [byLanguage, setByLanguage] = useState(false);
    const [toSort, setToSort] = useState([{}]);

    useEffect(() => {
        countries.filter((val) => {
            if (searchTerm === "") {
                return ""
            } else if (val.name.toLowerCase().includes(searchTerm.toLowerCase())){
                return setSearchTerm(val)
            }
        })

        console.log(toSort);
    }, [searchTerm])

    return (
        <div className="search">
            <h1>Country Search</h1>
            <span> Some random text </span>
            <div className="searchResults">
                <input type="text" placeholder="Search country..." onChange={event => {
                    setSearchTerm(event.target.value)
                }}/>
                <div className="groupBy">
                    <h2> Group by: </h2>
                    <div>
                        <button onClick={() => {
                            setByContinent(true) 
                            setByLanguage(false)}}> Continent </button>

                        <button onClick={() => {
                            setByLanguage(true);
                            setByContinent(false);
                            }}> Language </button>
                    </div>
                </div>

                <div>

                </div>
            </div>
        </div>
    )
}
export default CountrySearch;







