import React, { useEffect, useState } from "react";
import "./CountrySearch.css";

const CountrySearch = ({countries}) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [groupBy, setGroupBy] = useState([]);
  const [groupCondition, setGroupCondition] = useState(true);

  const handleClick = (n) => {
    if (n === 1) {
      setGroupCondition(true);
    } else if (n === 0) {
      setGroupCondition(false);
    }
  }

  useEffect(() => {
    if (groupCondition) {
      // search by continent
      setGroupBy(Object.entries(
        countries.filter((val) => {
          if (searchTerm === "") {
            return ""
          } else if (val.name.toLowerCase().includes(searchTerm.toLowerCase())){
            return val
          }
        }).reduce((res, val) => {
          if (!res[val.continent.name]) {
            res[val.continent.name] = [];
          }
          res[val.continent.name].push(val);
          return res;
        }, {})
      ))  
    } else if (!groupCondition) {
      // search by languages
      setGroupBy(Object.entries(
        countries.filter((val) => {
          if (searchTerm === "") {
            return ""
          } else if (val.name.toLowerCase().includes(searchTerm.toLowerCase())){
            return val
          }
        }).reduce((res, val) => {
          val.languages.map(lan => {
            const group = res[lan.name] || [];
            group.push(val);
            res[lan.name] = group;
          })
          return res;
        }, {})
      ))  
    }
  }, [searchTerm, groupCondition])

  console.log(groupBy);

  return (
    <div className="search">
      <h1>Country Search</h1>
      <span> Some random text </span>
      <div className="searchResults">
        <input type="text" placeholder="Type to search country . . ." onChange={event => {
          setSearchTerm(event.target.value)
        }}/>
        <div className="groupBy">
          <h2> Group by: </h2>
          <div>
            <button onClick={() => handleClick(1)}> Continent </button>
            <button onClick={() => handleClick(0)}> Language </button>
          </div>
        </div>
        <div>
          {
            groupBy.map(([continent, countries]) => (
              <div className="test" key={continent}>
                <h2>{continent}</h2>
                <div className="countriesContainer">
                  {countries.map(({name, languages, emoji, capital, currency}) => (
                    <div className="countryInfo" key={name}>
                      <div>
                        <span>{emoji}</span>
                        <h3> {name} </h3>
                      </div>
                      <p> Capital: {capital} </p>
                      <p> Currency: {currency} </p>
                      <div> <span> Languages: </span>
                      {languages.map(({ name }) => (
                        <span key={name}> {name}</span>
                      ))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))
          }
        </div>
      </div>
    </div>
  );
}
export default CountrySearch;

