import React, { useEffect, useState } from "react";
import "./CountrySearch.css";

const CountrySearch = ({countries}) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [groupBy, setGroupBy] = useState([]);

  useEffect(() => {
    // search by continent
    setGroupBy(Object.entries(
      countries.filter((val) => {
        if (searchTerm === "") {
          return ""
        } else if (val.name.toLowerCase().includes(searchTerm.toLowerCase())){
          return val
        }
      }).reduce((res, val) => {
        if (!res[val.continent.name]) {
          res[val.continent.name] = [];
        }
        res[val.continent.name].push(val);
        return res;
      }, {})
    ))  

    // search by languages

    // setGroupBy(Object.entries(
    //   countries.filter((val) => {
    //     if (searchTerm === "") {
    //       return ""
    //     } else if (val.name.toLowerCase().includes(searchTerm.toLowerCase())){
    //       return val
    //     }
    //   }).reduce((res, val) => {
    //     val.languages.map(lan => {
    //       const group = res[lan.name] || [];
    //       group.push(val);
    //       res[lan.name] = group;
    //     })
    //     return res;
    //   }, {})
    // ))  
  }, [searchTerm])

  console.log(groupBy);

  return (
    <div className="search">
      <h1>Country Search</h1>
      <span> Some random text </span>
      <div className="searchResults">
        <input type="text" placeholder="Search country..." onChange={event => {
          setSearchTerm(event.target.value)
        }}/>
        <div className="groupBy">
          <h2> Group by: </h2>
          <div>
            <button> Continent </button>
            <button> Language </button>
          </div>
        </div>
        <div>
          {
            groupBy.map(([continent, countries]) => (
              <div key={continent}>
                <h2>{continent}</h2>
                <div>
                  {countries.map(({name, languages, emoji, capital, currency}) => (
                    <div className="countryInfo" key={name}>
                      <div>
                        <span>{emoji}</span>
                        <h3> {name} </h3>
                      </div>
                      <p> Capital: {capital} </p>
                      <p> Currency: {currency} </p>
                      <ul>
                        {languages.map(({ name }) => (
                          <li key={name}>{name}</li>
                        ))}
                      </ul>
                    </div>
                  ))}
                </div>
              </div>
            ))
          }
        </div>
      </div>
    </div>
  );
}
export default CountrySearch;







